<script setup>
import IconsTelephone from '~/components/icons/telephone.vue';
import IconsMail from '~/components/icons/mail.vue';
import IconsLocation from '~/components/icons/location.vue';
import LogoSmall from './icons/logo-small.vue';

const { t } = useI18n();
const { $gsap } = useNuxtApp();
const route = useRoute();

const links = computed(() => [
  {
    to: '/about',
    label: t('nav.about'),
    isMulti: true
  },
  {
    to: '/participants',
    label: t('nav.participants')
  },
  {
    to: '/speakers',
    label: t('nav.speakers')
  },
  {
    to: '/partners',
    label: t('nav.partners')
  },
  {
    to: '/sponsors',
    label: t('nav.sponsors')
  },
  {
    to: '/media',
    label: t('nav.media'),
    isMulti: true
  }
]);
const contacts = [
  {
    icon: IconsTelephone,
    text: '+777 123 88 71',
    type: 'tel'
  },
  {
    icon: IconsMail,
    text: 'Example@gmail.com',
    type: 'mail'
  },
  {
    icon: IconsLocation,
    text: t('contacts.address'),
    type: 'location'
  }
];

const footerBg = computed(() => {
  if (route.path === '/') return '#fff';
  if (route.path === '/about') return '#F1F2F4';
  if (route.path === '/banks') return '#F8F8F8';
  else return '#F1F2F4';
});

onMounted(() => {
  const parentId = '#footer';
  const parentContainer = `${parentId} .footer__wrapper`;
  const travelDistance = 100;
  $gsap.utils.toArray(`${parentContainer}>*`).forEach((child, i) => {
    $gsap.from(child, {
      x: i % 2 === 0 ? travelDistance : -travelDistance,
      ...fadeOnScrollTrigger(child, null, null, false)
    });
  });
});
</script>

