<script setup>
const TIME_TO_LOAD = 3; // 3 seconds
const MAX_PROGRESS = 100; // 100%
const progress = ref(0);

defineProps({
  isLoaded: Boolean
});
const emits = defineEmits(['loaded']);

let interval;
onMounted(() => {
  const bar = document.querySelector('.preloader__bar--inner');
  const progressNumber = document.querySelector('.preloader__progress');
  const xVal = window.innerWidth > 576 ? 'calc(80vw - 117px)' : 'calc(100vw - 40px - 90px)';

  bar.animate([{ transform: 'scaleX(0)' }, { transform: 'scaleX(1)' }], {
    duration: TIME_TO_LOAD * 1000,
    fill: 'forwards',
    easing: 'ease-in-out'
  });
  progressNumber.animate([{ transform: 'translateX(0)' }, { transform: `translateX(${xVal})` }], {
    duration: TIME_TO_LOAD * 1000,
    fill: 'forwards',
    easing: 'ease-in-out'
  });

  interval = setInterval(() => {
    if (progress.value >= MAX_PROGRESS - 1) {
      clearInterval(interval);
      setTimeout(() => {
        emits('loaded');
      }, 150);
    }
    progress.value++;
  }, TIME_TO_LOAD * 10);
});
</script>

